import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const itemsData = [
  {
    id: 1,
    name: 'Broken Computer',
    description: 'Mid Working Condition 50 items',
    price: 45,
    image: 'https://media.istockphoto.com/id/171242093/photo/obsolete-electronics.jpg?s=2048x2048&w=is&k=20&c=N_24vtKhTNXd1yqqIn8OyWjIQgF2JAYkjiJpjQV34Fw=',
  },
  {
    id: 2,
    name: 'Old Mobile Phones',
    description: 'Various Brands 100 items',
    price: 25,
    image: 'data:image/jpeg;base64,',
  },
  {
    id: 3,
    name: 'Used Chargers and Cables',
    description: 'Working Condition 200 items',
    price: 10,
    image: 'data:image/jpeg;base64,',
  },
  {
    id: 4,
    name: 'Damaged Tablets',
    description: 'Minor Screen Issues 30 items',
    price: 60,
    image: 'data:image/jpeg;base64,',
  },
  {
    id: 5,
    name: 'Old Printers',
    description: 'Needs Repair 20 items',
    price: 100,
    image: 'data:image/jpeg;base64,',
  },
];

const Items = () => {
  const [cart, setCart] = useState([]);
  const [quantities, setQuantities] = useState({});

  const handleQuantityChange = (id, value) => {
    setQuantities((prev) => ({
      ...prev,
      [id]: Math.max(1, value),
    }));
  };

  const addToCart = (item) => {
    const quantity = quantities[item.id] || 1;
    const existingItem = cart.find((cartItem) => cartItem.id === item.id);

    if (existingItem) {
      const updatedCart = cart.map((cartItem) =>
        cartItem.id === item.id
          ? { ...cartItem, quantity: cartItem.quantity + quantity }
          : cartItem
      );
      setCart(updatedCart);
    } else {
      setCart([...cart, { ...item, quantity }]);
    }
  };

  const removeFromCart = (id) => {
    const updatedCart = cart.filter((item) => item.id !== id);
    setCart(updatedCart);
  };

  const clearCart = () => {
    setCart([]);
  };

  const calculateTotal = () =>
    cart.reduce((total, item) => total + item.price * item.quantity, 0);

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">E-Waste Marketplace</h2>

      <div className="row">
        {/* Product Cards Section */}
        <div className="col-md-8">
          <div className="row">
            {itemsData.map((item) => (
              <div className="col-md-6 mb-4" key={item.id}>
                <div className="card" style={{ width: '22rem' }}>
                  <img src={item.image} className="card-img-top" alt={item.name} />
                  <div className="card-body">
                    <h5 className="card-title">{item.name}</h5>
                    <p className="card-text">{item.description}</p>
                    <p className="card-text">Price: Rs {item.price}/item</p>
                    <div className="d-flex align-items-center mb-2">
                      <label className="me-2">Quantity:</label>
                      <input
                        type="number"
                        className="form-control"
                        style={{ width: '80px' }}
                        value={quantities[item.id] || 1}
                        min="1"
                        onChange={(e) =>
                          handleQuantityChange(item.id, parseInt(e.target.value))
                        }
                      />
                    </div>
                    <div className="d-flex justify-content-center">
                      <button
                        className="btn btn-sm btn-outline-primary"
                        onClick={() => addToCart(item)}
                      >
                        Add to Cart
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Cart Summary Section */}
        <div className="col-md-4">
          <h3>Cart Summary</h3>
          {cart.length === 0 ? (
            <p>No items in cart.</p>
          ) : (
            <div className="table-responsive">
              <table className="table table-bordered">
                <thead>
                  <tr>
                    <th>Item</th>
                    <th>Qty</th>
                    <th>Total</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {cart.map((item) => (
                    <tr key={item.id}>
                      <td>{item.name}</td>
                      <td>{item.quantity}</td>
                      <td>Rs {item.price * item.quantity}</td>
                      <td>
                        <button
                          className="btn btn-sm btn-danger"
                          onClick={() => removeFromCart(item.id)}
                        >
                          Remove
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
              <h4 className="text-end">Total: Rs {calculateTotal()}</h4>
              <div className="d-flex justify-content-end">
                <button className="btn btn-outline-danger" onClick={clearCart}>
                  Clear Cart
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Items;
